import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import logo from '../reminder_logo.svg';
import axios from 'axios';
import {Route} from 'react-router-dom';
import Verify from './verify.component';

class Register extends Component {
    constructor(props) {
        super(props);

        this.change = this.change.bind(this);
        this.getNumber = this.getNumber.bind(this);
        this.state = {
          number: "",
          resultId: ""
        }
    }

    componentDidMount() {
      const div = document.getE
      const script = document.createElement('script');
      script.src = 'https://telegram.org/js/telegram-widget.js?9';
      script.setAttribute('data-telegram-login', "akiranBot");
      script.setAttribute('data-size', 'large');
      document.body.appendChild(script);
    }

    getNumber(event) {
        this.setState({
          number: event.target.value
        })
    }

    change(event){
      event.preventDefault();
      const number = {
        number: this.state.number
      }

      console.log(number)
      axios.post('http://localhost:4000/register', number)
            .then(res => {
              this.setState({
                  resultId: res.data.resultId
              })
            })
            .catch(err => console.log(err));

      this.props.history.push('/verify');
    }

    render() {
        return <div className="container">
      <div className="container centering">
      {/* <img src={logo} alt="Logo" className="image-left"></img> */}
      </div>
      <div className="centering">
        <h5>Get Reminded!!</h5>
        <div ref="script"></div>
          <div class="input-group mb-3">
      <div class="input-group-append">
      <input type="text" 
      placeholder =  "Enter 10-digit phone number"
      onChange={this.getNumber}
      class="search-query input-mysize"/>
      </div>
      <span class="input-group-btn input-space">
      <button className="btn btn-primary loginButton" 
      type="button" onClick={this.change}>Generate OTP</button>
      <Route exact path='/verify' render={(routeProps) => (<Verify {...routeProps} resultId={this.state.resultId}/>)}/>
      </span>
     </div>
        </div>
      </div>
    }
}

export default Register;
