import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import logo from '../reminder_logo.svg';
import TelegramLoginButton from './TelegramLoginButton';

class Register extends Component {
    constructor(props) {
        super(props);
        this.state = {
          number: "",
          resultId: ""
        }

        const responseTelegram = response => {
  console.log(response);
};
    }

    componentDidMount() {
      window.dataOnauth = function (user) {
        console.log(user);
    };
      const div = document.getElementById('log');
      const script = document.createElement('script');
      script.src = 'https://telegram.org/js/telegram-widget.js?9';
      script.setAttribute('data-telegram-login', "akiranBot");
      script.setAttribute('data-size', 'large');
      script.setAttribute('data-onauth', 'dataOnauth(user)');
      div.appendChild(script);
    }

    render() {
        return <div className="container">
          <div className="container centering">
            <img src={logo} alt="Logo" className="image-left"></img>
      </div>
      <div className="centering">
      <h2>Reminders made easy !!</h2>
      <TelegramLoginButton dataOnauth={this.responseTelegram} botName="OdauBot" />
        </div>
      </div>
    }
}

export default Register;
