import React, { Component } from 'react';
import axios from 'axios';
class CreateReminders extends Component {
    constructor(props) {
        super(props);
    }
    componentWillMount() {

    }
    render() {
        return(
            <div style={{marginTop: 10}}>
                <h3>Create New Reminder</h3>
                <form onSubmit={this.onSubmit}>
                <div className="form-group">
                    <label>Description:</label>
                    <input type="text" className="form-control" value={this.state.todo_description} onChange={this.onChangeTodoDescription}/>
                </div>
                <div className="form-group">
                    <label>Responsible:</label>
                    <input type="text" className="form-control" value={this.state.todo_responsible} onChange={this.onChangeTodoResponsible}></input>
                </div>

                <div className="form-group">
                    <input type="submit" value="Create Todo" className="btn btn-primary"></input>
                </div>
                </form>
            </div>
        )
    }
}

export default CreateReminders;
